<!--
  @component
  A variant of `<RadioGroup>`, looking like normal buttons.
  @see https://w3c.github.io/aria/#radiogroup
  @see https://www.w3.org/WAI/ARIA/apg/patterns/radio/
-->
<script>
  import { activateGroup } from '../../services/group.svelte';

  /**
   * @typedef {object} Props
   * @property {string} [class] - The `class` attribute on the wrapper element.
   * @property {boolean} [hidden] - Whether to hide the widget.
   * @property {boolean} [disabled] - Whether to disable the widget. An alias of the `aria-disabled`
   * attribute.
   * @property {boolean} [readonly] - Whether to make the widget read-only. An alias of the
   * `aria-readonly` attribute.
   * @property {boolean} [required] - Whether to mark the widget required. An alias of the
   * `aria-required` attribute.
   * @property {boolean} [invalid] - Whether to mark the widget invalid. An alias of the
   * `aria-invalid` attribute.
   * @property {import('svelte').Snippet} [children] - Primary slot content.
   * @property {(event: CustomEvent) => void} [onChange] - Custom `Change` event handler.
   */

  /**
   * @type {Props & Record<string, any>}
   */
  let {
    /* eslint-disable prefer-const */
    class: className,
    hidden = false,
    disabled = false,
    readonly = false,
    required = false,
    invalid = false,
    children,
    ...restProps
    /* eslint-enable prefer-const */
  } = $props();
</script>

<div
  {...restProps}
  role="radiogroup"
  class="sui select-button-group {className}"
  {hidden}
  tabindex="-1"
  aria-hidden={hidden}
  aria-disabled={disabled}
  aria-readonly={readonly}
  aria-required={required}
  aria-invalid={invalid}
  use:activateGroup
>
  <div role="none" class="inner" inert={disabled}>
    {@render children?.()}
  </div>
</div>

<style>.select-button-group {
  display: inline-flex;
  align-items: center;
  margin: var(--sui-focus-ring-width);
}
.select-button-group:focus-visible {
  outline-width: 0 !important;
}
.select-button-group :global(button) {
  margin: 0 !important;
  border-radius: 0 !important;
  color: var(--sui-primary-foreground-color);
}
.select-button-group :global(button):first-child {
  border-top-left-radius: 4px !important;
  border-bottom-left-radius: 4px !important;
}
.select-button-group :global(button):not(:first-child) {
  border-left-width: 0;
}
.select-button-group :global(button):last-child {
  border-top-right-radius: 4px !important;
  border-bottom-right-radius: 4px !important;
}
.select-button-group[aria-invalid=true] :global(button) {
  border-color: var(--sui-error-border-color);
}
.select-button-group :global(button[aria-checked="true"]) {
  color: var(--sui-highlight-foreground-color);
  background-color: var(--sui-selected-background-color);
}
.select-button-group[aria-disabled=false] :global(button[aria-disabled="true"]) {
  filter: grayscale(0) opacity(1);
}
.select-button-group[aria-disabled=false] :global(button[aria-disabled="true"]) :global(*) {
  filter: grayscale(1) opacity(0.35);
}

.inner {
  display: contents;
}</style>
